function update(dt){
    if(isGameOver||isPaused||isLevelUp){
        // 暂停时仍然吸收经验,但速度更快
        if(player&&xpGems.length>0){
            xpGems.forEach(g=>g.update(dt*4.5,player));
        }
        return;
    }

    gameTime+=dt;
    difficultyTimer+=dt;
    spawnTimer+=dt;

    // 难度调整
    if(difficultyTimer>10){
        currentSpawnInterval=Math.max(0.08, baseSpawnInterval*Math.pow(0.975, gameTime/60)); // 减缓难度提升
        difficultyTimer=0;
    }

    if(spawnTimer>=currentSpawnInterval){
        const sc=1+Math.floor(gameTime/35); // 减缓敌人数量增长
        for(let i=0;i<sc;i++) spawnEnemy();
        spawnTimer=0;
    }

    // ... 其他更新逻辑保持不变 ...
    player.update(dt,keys);
    enemies.forEach(e=>e.update(dt,player));
    projectiles.forEach(p=>p.update(dt));
    xpGems.forEach(g=>g.update(dt,player));
    worldObjects.forEach(o=>o.update(dt,player));
    damageNumbers.forEach(d=>d.update(dt));

    // *** 对象池回收逻辑 ***
    for (let i = projectiles.length - 1; i >= 0; i--) {
        if (projectiles[i].isGarbage) {
            const proj = projectiles.splice(i, 1)[0];
            proj.isActive = false;
            inactiveProjectiles.push(proj);
        }
    }
    for (let i = damageNumbers.length - 1; i >= 0; i--) {
        if (damageNumbers[i].isGarbage) {
            const dn = damageNumbers.splice(i, 1)[0];
            dn.isActive = false;
            inactiveDamageNumbers.push(dn);
        }
    }
    enemies=enemies.filter(e=>!e.isGarbage);
    xpGems=xpGems.filter(g=>!g.isGarbage);
    worldObjects=worldObjects.filter(o=>!o.isGarbage);

    if(isLevelUp){presentLevelUpOptions();isLevelUp=false;}
    updateUI();
}
