let lastDrawTime = 0;
const FRAME_TIME = 1000/60; // 60fps

function gameLoop(t){
    if(isGameOver){
        animationFrameId=null;
        return;
    }

    // 计算时间增量
    deltaTime=(t-lastTime)/1000;
    lastTime=t;
    deltaTime=Math.min(deltaTime, 0.1);

    // 更新游戏状态
    update(deltaTime);

    // 控制绘制频率
    if(t - lastDrawTime >= FRAME_TIME){
        draw();
        lastDrawTime = t;
    }

    animationFrameId=requestAnimationFrame(gameLoop);
}
