function draw(){
    try {
        // 使用双缓冲绘制
        const bufferCanvas = document.createElement('canvas');
        bufferCanvas.width = GAME_WIDTH;
        bufferCanvas.height = GAME_HEIGHT;
        const bufferCtx = bufferCanvas.getContext('2d');

        bufferCtx.clearRect(0, 0, GAME_WIDTH, GAME_HEIGHT);

        // 在缓冲画布上绘制所有内容
        xpGems.forEach(g=>g.draw(bufferCtx));
        worldObjects.forEach(o=>o.draw(bufferCtx));
        enemies.forEach(e=>e.draw(bufferCtx));
        damageNumbers.forEach(d=>d.draw(bufferCtx));
        projectiles.forEach(p=>p.draw(bufferCtx));
        player.weapons.forEach(w=>{
            if(w.drawAura) w.drawAura(bufferCtx,player);
            if(w.drawHitboxes) w.drawHitboxes(bufferCtx);
        });
        player.draw(bufferCtx);

        // 一次性将缓冲内容复制到主画布
        ctx.clearRect(0, 0, GAME_WIDTH, GAME_HEIGHT);
        ctx.drawImage(bufferCanvas, 0, 0);

    } catch (error) {
        console.error("绘制过程中发生错误:", error);
    }
}
