            // --- CORRECTED getStat Method ---
            getStat(statName) {
                // Determine the base value from defaults. Multipliers usually start at 1.
                let baseValue = PLAYER_DEFAULT_STATS[statName] ?? (statName.endsWith('Multiplier') || statName === 'luck' ? 1.0 : 0.0);

                let addBonus = 0;         // Total additive bonus from items
                let multBonusPercent = 0; // Total multiplicative bonus percentage (e.g., 0.1 for +10%)

                // Accumulate bonuses from passive items
                this.passiveItems.forEach(item => {
