                      const barHeight = 8;
                      const barX = this.x - barWidth / 2;
                      const barY = this.y - this.size / 1.5 - barHeight; // Position above emoji
                      const healthPercent = this.health / this.stats.health;

                      context.fillStyle = '#555';
                      context.fillRect(barX, barY, barWidth, barHeight);
                      context.fillStyle = '#e74c3c';
                      context.fillRect(barX, barY, barWidth * healthPercent, barHeight);
                      context.strokeStyle = '#333';
                      context.strokeRect(barX, barY, barWidth, barHeight);
                 }
             }
        }


        class Projectile extends GameObject {
             constructor(x, y, emoji, size, vx, vy, damage, pierce, duration, ownerStats) {
                super(x, y, emoji, size);
                this.vx = vx;
                this.vy = vy;
                this.damage = damage * ownerStats.damageMultiplier;
                this.pierce = pierce; // How many enemies it can hit before disappearing
                this.duration = duration; // How long it lasts in seconds
                this.lifetime = 0;
                this.ownerStats = ownerStats; // Store owner's stats at time of firing
                this.hitTargets = new Set(); // Keep track of enemies already hit by this projectile instance
             }

             update(dt) {
                 this.x += this.vx * dt;
                 this.y += this.vy * dt;
                 this.lifetime += dt;

                 if (this.lifetime >= this.duration || this.pierce <= 0) {
                     this.isGarbage = true;
                     return;
                 }

                 // Check collision with enemies
                 enemies.forEach(enemy => {
                     if (this.isGarbage || enemy.isGarbage || this.hitTargets.has(enemy)) return;

                     if (this.checkCollision(enemy)) {
                          const isDead = enemy.takeDamage(this.damage, player); // Assume player is owner for kill credit
                          this.hitTargets.add(enemy); // Mark as hit
                          this.pierce--;
                          if (this.pierce <= 0) {
                              this.isGarbage = true;
                          }
                          // Optional: Add hit effect
                     }
                 });

                 // Boundary check (remove if it goes too far off screen)
                 if (this.x < -this.size * 2 || this.x > GAME_WIDTH + this.size * 2 ||
                     this.y < -this.size * 2 || this.y > GAME_HEIGHT + this.size * 2) {
                     this.isGarbage = true;
                 }
             }
        }

         class ExperienceGem extends GameObject {
            constructor(x, y, value) {
                super(x, y, EMOJI.XP_GEM, GAME_FONT_SIZE * 0.6);
                this.value = value;
                this.attractionSpeed = 400; // Speed towards player when close
            }

            update(dt, target) { // Target is the player
                 if (this.isGarbage) return;
                 const pickupRadiusSq = target.pickupRadiusSq; // Use player's potentially boosted radius
                 const distSq = this.distanceSq(target);

                 // Move towards player if within attraction radius
                 if (distSq < pickupRadiusSq && distSq > 1) { // Don't divide by zero if exactly on player
                    const dist = Math.sqrt(distSq);
                    const dx = target.x - this.x;
                    const dy = target.y - this.y;
                    // Accelerate faster when closer?
                    const speedMultiplier = 1 + (pickupRadiusSq - distSq) / pickupRadiusSq * 2;
                    this.x += (dx / dist) * this.attractionSpeed * speedMultiplier * dt;
                    this.y += (dy / dist) * this.attractionSpeed * speedMultiplier * dt;
                 }

                 // Check for actual pickup (closer collision)
                 if (this.checkCollision(target)) {
                     target.gainXP(this.value);
                     this.isGarbage = true;
                     // Optional: Add pickup sound/effect
                 }
            }
        }

        class Pickup extends GameObject {
             constructor(x, y, emoji, type, value) {
                 super(x, y, emoji, GAME_FONT_SIZE);
                 this.type = type; // 'heal', 'magnet', etc.
                 this.value = value;
                 this.lifetime = 10; // Disappears after 10 seconds
                 this.attractionSpeed = 300;
             }

             update(dt, target) { // Target is the player
                 this.lifetime -= dt;
                 if (this.lifetime <= 0) {
                     this.isGarbage = true;
                     return;
                 }

                 const pickupRadiusSq = target.pickupRadiusSq;
                 const distSq = this.distanceSq(target);

                 if (distSq < pickupRadiusSq && distSq > 1) {
                     const dist = Math.sqrt(distSq);
                     const dx = target.x - this.x;
                     const dy = target.y - this.y;
                     this.x += (dx / dist) * this.attractionSpeed * dt;
                     this.y += (dy / dist) * this.attractionSpeed * dt;
                 }

                 if (this.checkCollision(target)) {
                     this.applyEffect(target);
                     this.isGarbage = true;
                 }
             }

             applyEffect(target) {
                 switch (this.type) {
                     case 'heal':
