         // --- Weapon & Item System ---

         class UpgradeableItem {
             constructor(name, emoji, maxLevel = 8) {
                 this.name = name;
                 this.emoji = emoji;
                 this.level = 1;
                 this.maxLevel = maxLevel;
                 this.owner = null; // Will be set when added to player
             }

             getLevel() { return this.level; }
             isMaxLevel() { return this.level >= this.maxLevel; }

             upgrade() {
                 if (!this.isMaxLevel()) {
                     this.level++;
                     console.log(`${this.name} upgraded to level ${this.level}`);
                     this.onUpgrade(); // Apply stat changes for the new level
                     return true;
                 }
                 return false;
             }

             // Abstract methods to be implemented by subclasses
             onUpgrade() { /* Recalculate stats based on new level */ }
             getUpgradeDescription() { return `Increase ${this.name} effectiveness.`; } // Default description
             getBaseUpgradeOptions(player) { // Options when getting this item for the first time
                  return [{
                      item: this, // The instance to potentially add
                      type: this instanceof Weapon ? 'new_weapon' : 'new_passive',
                      text: `Acquire ${this.name}`,
                      description: this.getInitialDescription ? this.getInitialDescription() : `Adds the ${this.name}.`,
                      icon: this.emoji,
                      action: () => {
                          if (this instanceof Weapon) player.addWeapon(this);
                          else player.addPassive(this);
                      }
                  }];
             }
             getCurrentUpgradeOptions(player) { // Options when upgrading this existing item
                  if (this.isMaxLevel()) return []; // No upgrades if max level

                  return [{
                      item: this,
                      type: this instanceof Weapon ? 'upgrade_weapon' : 'upgrade_passive',
                      text: `Upgrade ${this.name}`,
                      description: this.getUpgradeDescription(),
                      icon: this.emoji,
                      level: this.level + 1, // Show next level
                      action: () => {
                          this.upgrade();
                          // Check for evolution *after* upgrading
                          if (this instanceof Weapon) checkEvolution(player, this);
                      }
                  }];
             }
         }

         class Weapon extends UpgradeableItem {
             constructor(name, emoji, baseCooldown, maxLevel) {
                 super(name, emoji, maxLevel);
                 this.baseCooldown = baseCooldown;
                 this.cooldownTimer = 0;
                 this.stats = {}; // Weapon specific stats like damage, area, count etc.
                 this.calculateStats(); // Initial calculation
             }

             update(dt, owner) {
                 this.cooldownTimer -= dt * owner.getStat('cooldownMultiplier'); // Apply global cooldown reduction
                 if (this.cooldownTimer <= 0) {
                     this.fire(owner);
                     this.cooldownTimer = this.stats.cooldown;
                 }
             }

             // Abstract - implement in specific weapons
             fire(owner) { console.warn(`${this.name} fire() not implemented`); }
             calculateStats() { console.warn(`${this.name} calculateStats() not implemented`); }

             getOwnerStats(owner) {
                 // Consolidate player stats relevant to this weapon at the moment of firing
                 return {
                     x: owner.x,
                     y: owner.y,
                     damageMultiplier: owner.getStat('damageMultiplier'),
                     areaMultiplier: owner.getStat('areaMultiplier'),
                     durationMultiplier: owner.getStat('durationMultiplier'),
                     projectileSpeedMultiplier: owner.getStat('projectileSpeedMultiplier'),
                     projectileCountBonus: owner.getStat('projectileCountBonus') || 0, // Ensure it exists
                     luck: owner.luck,
                     lastMoveDirection: owner.lastMoveDirection,
                 };
             }
         }

        class PassiveItem extends UpgradeableItem {
            constructor(name, emoji, maxLevel, bonuses = {}) {
                super(name, emoji, maxLevel);
                // Bonuses structure: { statName: { add: value, mult: value }, ... }
                // Example: { damageMultiplier: { mult: 0.1 } } // 10% damage increase at level 1
                this.baseBonuses = bonuses; // Store the per-level definition
                this.bonuses = {}; // Actual calculated bonuses for current level
                this.calculateStats(); // Calculate for level 1 initially
            }

            applyInitialBonus(player) {
                 // Called when item is first added
                 this.calculateStats(); // Ensure stats are calculated for level 1
                 player.recalculateStats(); // Trigger player stat update
            }

            onUpgrade() {
                 this.calculateStats(); // Recalculate bonuses for the new level
                 if (this.owner) {
                     this.owner.recalculateStats(); // Update player stats immediately
                     // Also check for evolution involving this passive
                     checkEvolution(this.owner, this);
                 }
            }

            // Example: Needs to be specific per item based on baseBonuses
            calculateStats() {
                 this.bonuses = {};
                 for (const stat in this.baseBonuses) {
                      const base = this.baseBonuses[stat];
                      this.bonuses[stat] = {};
                      if (base.add) this.bonuses[stat].add = base.add * this.level;
                      if (base.mult) this.bonuses[stat].mult = base.mult * this.level;
                 }
            }

             getUpgradeDescription() {
                 // Generate description based on next level's bonus improvement
                 // This requires defining how bonuses scale per level in baseBonuses
                 // Simplified:
                 let desc = `Improves ${this.name}. `;
                 // Example: if it boosts damageMultiplier
                 if (this.baseBonuses.damageMultiplier?.mult) {
                     desc += ` (+${(this.baseBonuses.damageMultiplier.mult * 100).toFixed(0)}% Damage/Lv)`;
                 }
                  if (this.baseBonuses.maxHealth?.add) {
                     desc += ` (+${this.baseBonuses.maxHealth.add} Max HP/Lv)`;
                 }
                 // Add more specific descriptions based on baseBonuses structure
                 return desc;
             }
             getInitialDescription() {
                 // Similar to getUpgradeDescription but for Lv 1 effect
                 let desc = `Grants ${this.name}. `;
                  if (this.baseBonuses.damageMultiplier?.mult) {
                     desc += ` (+${(this.baseBonuses.damageMultiplier.mult * 100).toFixed(0)}% Damage)`;
                 }
                 if (this.baseBonuses.maxHealth?.add) {
                     desc += ` (+${this.baseBonuses.maxHealth.add} Max HP)`;
                 }
                 return desc;
             }
        }

