        function spawnEnemy() {
             if (currentBoss) return; // Don't spawn normal enemies while boss is active

             // Calculate total weight of spawnable enemies
             let totalWeight = 0;
             const spawnableTypes = ENEMY_TYPES.filter(type => gameTime >= (type.minTime || 0));
             spawnableTypes.forEach(type => totalWeight += type.weight);

             if (totalWeight <= 0) return; // No enemies available to spawn yet

             // Choose enemy type based on weight
             let randomWeight = Math.random() * totalWeight;
             let chosenType = spawnableTypes[spawnableTypes.length - 1]; // Default to last if calculation fails
             for (const type of spawnableTypes) {
                 if (randomWeight < type.weight) {
                     chosenType = type;
                     break;
                 }
                 randomWeight -= type.weight;
             }


            let spawnX, spawnY;
            const edge = Math.floor(Math.random() * 4);
             const padding = 50; // Spawn slightly off-screen

            switch (edge) {
                case 0: spawnX = Math.random() * GAME_WIDTH; spawnY = -padding; break; // Top
                case 1: spawnX = GAME_WIDTH + padding; spawnY = Math.random() * GAME_HEIGHT; break; // Right
                case 2: spawnX = Math.random() * GAME_WIDTH; spawnY = GAME_HEIGHT + padding; break; // Bottom
                case 3: spawnX = -padding; spawnY = Math.random() * GAME_HEIGHT; break; // Left
            }

             // Difficulty scaling based on time
             const difficultyMultiplier = 1 + (gameTime / 120); // Example: ramps up every 2 minutes

             enemies.push(new Enemy(spawnX, spawnY, chosenType, difficultyMultiplier));
        }

        function spawnBoss() {
             console.log("Attempting to spawn boss...");
             currentBoss = null; // Clear previous just in case

             // Spawn boss near an edge, away from player if possible
             let spawnX, spawnY;
             const edge = Math.floor(Math.random() * 4);
             const padding = 100;

             // Try to spawn opposite the player's quadrant
             const playerQuadrantX = player.x < GAME_WIDTH / 2 ? 0 : 1;
             const playerQuadrantY = player.y < GAME_HEIGHT / 2 ? 0 : 1;
             let targetEdge = edge;
             if (playerQuadrantX === 0 && playerQuadrantY === 0) targetEdge = Math.random() < 0.5 ? 1 : 2; // Spawn Right or Bottom
             else if (playerQuadrantX === 1 && playerQuadrantY === 0) targetEdge = Math.random() < 0.5 ? 3 : 2; // Spawn Left or Bottom
             else if (playerQuadrantX === 0 && playerQuadrantY === 1) targetEdge = Math.random() < 0.5 ? 1 : 0; // Spawn Right or Top
             else targetEdge = Math.random() < 0.5 ? 3 : 0; // Spawn Left or Top


            switch (targetEdge) {
                 case 0: spawnX = Math.random() * GAME_WIDTH; spawnY = -padding; break;
                 case 1: spawnX = GAME_WIDTH + padding; spawnY = Math.random() * GAME_HEIGHT; break;
                 case 2: spawnX = Math.random() * GAME_WIDTH; spawnY = GAME_HEIGHT + padding; break;
