        function update(dt) {
            if (isGameOver || isPaused || isLevelUp) {
                 // Special case: Allow gem attraction even when paused/leveling up? (VS style)
                 if (player && xpGems.length > 0) {
                     xpGems.forEach(gem => gem.update(dt * 3, player)); // Faster attraction when paused
                 }
