            if (difficultyTimer > 10) { // Adjust difficulty every 10 seconds
                // Spawn faster over time, minimum interval 0.1s
                currentSpawnInterval = Math.max(0.1, baseSpawnInterval * Math.pow(0.95, gameTime / 60));
                difficultyTimer = 0;
            }

            if (spawnTimer >= currentSpawnInterval) {
                const spawnCount = 1 + Math.floor(gameTime / 30); // Spawn more enemies over time
                for(let i=0; i<spawnCount; i++) {
                    spawnEnemy();
                }
                spawnTimer = 0;
