             // --- Boss Spawning ---
             if (gameTime >= nextBossTime && !currentBoss) {
                 spawnBoss();
                 nextBossTime += BOSS_INTERVAL; // Schedule next boss
             }


            // --- Update Game Objects ---
            player.update(dt, keys);
            enemies.forEach(e => e.update(dt, player));
            projectiles.forEach(p => p.update(dt));
            xpGems.forEach(gem => gem.update(dt, player));
            worldObjects.forEach(obj => obj.update(dt, player));


            // --- Remove Garbage Objects ---
            // Filter arrays in place or create new ones
            enemies = enemies.filter(e => !e.isGarbage);
            projectiles = projectiles.filter(p => !p.isGarbage);
            xpGems = xpGems.filter(g => !g.isGarbage);
