                  xpValueUI.textContent = player.xp;
                  xpNextLevelValueUI.textContent = player.xpToNextLevel;
                  xpBarUI.style.width = `${(player.xp / player.xpToNextLevel) * 100}%`;
             } else {
                  xpValueUI.textContent = "MAX";
                  xpNextLevelValueUI.textContent = "";
                  xpBarUI.style.width = `100%`;
                  xpBarUI.style.backgroundColor = '#f1c40f'; // Gold color for max level
             }


             // Timer & Kills
             timerValueUI.textContent = formatTime(gameTime);
             killCountValueUI.textContent = killCount;

             // Weapon & Passive Icons
             weaponIconsUI.innerHTML = player.weapons.map(w =>
                 `<span class="uiIcon" title="${w.name}">${w.emoji}<span class="uiItemLevel">${w.isEvolved ? 'MAX' : 'Lv'+w.level}</span></span>`
             ).join(' ');
             passiveIconsUI.innerHTML = player.passiveItems.map(p =>
                  `<span class="uiIcon" title="${p.name}">${p.emoji}<span class="uiItemLevel">Lv${p.level}</span></span>`
             ).join(' ');
         }


        function gameOver() {
            isGameOver = true;
            if (animationFrameId) cancelAnimationFrame(animationFrameId);
            finalTimeUI.textContent = formatTime(gameTime);
            finalLevelUI.textContent = player.level;
             finalKillsUI.textContent = killCount;
            gameOverScreen.classList.remove('hidden');
        }

        function pauseGame(forLevelUp = false) {
             if (isGameOver) return;
             isPaused = true;
             if (!forLevelUp) {
                 pauseScreen.classList.remove('hidden');
             }
             // Don't cancel animation frame, just stop updating game logic
        }

        function resumeGame() {
            if (isGameOver) return;
            isPaused = false;
            isLevelUp = false; // Ensure this is reset
            levelUpScreen.classList.add('hidden');
            pauseScreen.classList.add('hidden');
            lastTime = performance.now(); // Prevent large deltaTime jump
            // Restart loop if it was somehow cancelled
             if (!animationFrameId) { // Should not happen if just paused, but safety check
                  gameLoop(lastTime);
             }
        }

        // --- Main Game Loop ---
        function gameLoop(timestamp) {
            deltaTime = (timestamp - lastTime) / 1000;
            lastTime = timestamp;

             // Clamp deltaTime to prevent spiral of death if tabbed out
             deltaTime = Math.min(deltaTime, 0.1); // Max delta 100ms (10fps)

            update(deltaTime);
            draw();

            // Request next frame if game isn't over
            if (!isGameOver) {
                 animationFrameId = requestAnimationFrame(gameLoop);
            } else {
                 animationFrameId = null; // Clear ID on game over
            }
        }

        // --- Utility Functions ---
        function shuffleArray(array) {
            for (let i = array.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [array[i], array[j]] = [array[j], array[i]];
            }
        }

        // --- Event Listeners ---
        window.addEventListener('keydown', (e) => {
            keys[e.key.toLowerCase()] = true;
             // Pause Toggle
             if (e.key.toLowerCase() === 'p') {
                 if (isPaused && !isLevelUp) { // Can only unpause via P if it wasn't a levelup pause
                     resumeGame();
                 } else if (!isPaused && !isLevelUp) { // Can only pause via P if not already paused/leveling
                     pauseGame();
                 }
             }
        });

        window.addEventListener('keyup', (e) => {
            keys[e.key.toLowerCase()] = false;
        });

         // Start/Restart Buttons
         startButton.addEventListener('click', () => {
            init();
        });
         restartButton.addEventListener('click', () => {
             init();
        });
         resumeButton.addEventListener('click', () => {
             resumeGame();
         });


        // Handle window resize
        window.addEventListener('resize', () => {
             GAME_WIDTH = Math.min(window.innerWidth * 0.95, 1280);
             GAME_HEIGHT = Math.min(window.innerHeight * 0.95, 720);
             canvas.width = GAME_WIDTH;
             canvas.height = GAME_HEIGHT;
             if (player) { // Adjust player boundary limits if game is running
                 player.x = Math.max(player.size / 2, Math.min(GAME_WIDTH - player.size / 2, player.x));
                 player.y = Math.max(player.size / 2, Math.min(GAME_HEIGHT - player.size / 2, player.y));
             }
             // No need to redraw immediately unless game is active and not paused
             if (player && !isGameOver && !isPaused && !isLevelUp) {
                 draw();
             }
        });

        // --- Initial Setup ---
        // Show start screen, game initialized on button click.
        console.log("Emoji Survivor Initialized. Press Start Game.");

    </script>
