export class GameObject {
    constructor(x, y, emoji, size = GAME_FONT_SIZE) {
        this.x = x;
        this.y = y;
        this.emoji = emoji;
        this.size = size; // Use size for collision approximation
        this.width = size; // AABB approximation
        this.height = size;
        this.isGarbage = false; // Flag for removal
    }

    draw(context) {
        context.font = `${this.size}px 'Segoe UI Emoji'`;
        context.fillText(this.emoji, this.x, this.y);
    }

    checkCollision(other) {
        // Simple AABB collision based on size
        const halfSize = this.size / 2;
        const otherHalfSize = other.size / 2;
        return (
            this.x - halfSize < other.x + otherHalfSize &&
            this.x + halfSize > other.x - otherHalfSize &&
            this.y - halfSize < other.y + otherHalfSize &&
            this.y + halfSize > other.y - otherHalfSize
        );
    }

    distanceSq(other) {
        const dx = this.x - other.x;
        const dy = this.y - other.y;
        return dx * dx + dy * dy;
    }

    update(dt) { /* Base update if needed */ }
}

