import { GameObject } from './GameObject.js';

export class Character extends GameObject {
    constructor(x, y, emoji, size, stats) {
        super(x, y, emoji, size);
        this.stats = { ...stats }; // Copy base stats
        this.health = this.stats.health;
        this.speed = this.stats.speed;
        this.damage = this.stats.damage || 0;
        this.xpValue = this.stats.xp || 0;
    }

    takeDamage(amount, source) {
        // Basic damage calculation, could add armor later
        const damageTaken = Math.max(1, amount - (this.stats.armor || 0));
        this.health -= damageTaken;
        this.health = Math.max(0, this.health);
        // TODO: Add damage numbers effect
        if (this.health <= 0) {
            this.onDeath(source);
            return true; // Is dead
        }
        return false; // Still alive
    }

    onDeath(killer) {
        this.isGarbage = true;
    }
}

