 ```
+ import { Character } from './Character.js';
+ import { EMOJI, GAME_FONT_SIZE, LEVEL_XP_REQUIREMENTS, MAX_LEVEL, PLAYER_DEFAULT_STATS } from '../utils/constants.js';
+ 
+ export class Player extends Character {
+     constructor(x, y) {
+         super(x, y, EMOJI.PLAYER, GAME_FONT_SIZE * 1.2, PLAYER_DEFAULT_STATS); // Player slightly larger
+         this.maxHealth = this.stats.health;
+         this.level = 1;
+         this.xp = 0;
+         this.xpToNextLevel = this.getXPForLevel(1);
+         this.weapons = [];
+         this.passiveItems = [];
+         this.maxWeapons = this.stats.maxWeapons;
+         this.maxPassives = this.stats.maxPassives;
+         this.pickupRadius = this.stats.pickupRadius; // Store base radius
+         this.pickupRadiusSq = this.pickupRadius * this.pickupRadius; // Initial calculation
+         this.luck = this.stats.luck;
+         this.regenTimer = 0;
+         this.moveDirection = { x: 0, y: 0 }; // For weapon aiming if needed
+         this.lastMoveDirection = { x: 1, y: 0 }; // Default aim direction
+         this.recalculateStats(); // Initial stat calculation including passives (none yet)
+     }
+ 
+     getXPForLevel(level) {
+         if (level - 1 < LEVEL_XP_REQUIREMENTS.length) {
+             return LEVEL_XP_REQUIREMENTS[level - 1];
+         }
+         return Math.floor(LEVEL_XP_REQUIREMENTS[LEVEL_XP_REQUIREMENTS.length - 1] * Math.pow(1.15, level - LEVEL_XP_REQUIREMENTS.length));
+     }
+ 
+     update(dt, keysPressed) {
+         if (this.isGarbage) return;
+         let dx = 0;
+         let dy = 0;
+         if (keysPressed['w'] || keysPressed['W'] || keysPressed['ArrowUp']) dy -= 1;
+         if (keysPressed['s'] || keysPressed['S'] || keysPressed['ArrowDown']) dy += 1;
+         if (keysPressed['a'] || keysPressed['A'] || keysPressed['ArrowLeft']) dx -= 1;
+         if (keysPressed['d'] || keysPressed['D'] || keysPressed['ArrowRight']) dx += 1;
+ 
+         const length = Math.sqrt(dx * dx + dy * dy);
+         let moveX = 0;
+         let moveY = 0;
+         const currentSpeed = this.getStat('speed'); // Use calculated speed
+ 
+         if (length > 0) {
+             moveX = (dx / length) * currentSpeed * dt;
+             moveY = (dy / length) * currentSpeed * dt;
+             this.moveDirection = { x: dx / length, y: dy / length };
+             this.lastMoveDirection = { ...this.moveDirection };
+         } else {
+             this.moveDirection = { x: 0, y: 0 };
+         }
+ 
+         this.x += moveX;
+         this.y += moveY;
+ 
+         // Keep player within game bounds
+         const gameWidth = window.innerWidth * 0.95;
+         const gameHeight = window.innerHeight * 0.95;
+         this.x = Math.max(this.size / 2, Math.min(gameWidth - this.size / 2, this.x));
+         this.y = Math.max(this.size / 2, Math.min(gameHeight - this.size / 2, this.y));
+ 
+         // Update weapons and passive items
+         this.weapons.forEach(weapon => weapon.update(dt, this));
+         this.passiveItems.forEach(item => item.update && item.update(dt, this));
+ 
+         // Handle regeneration
+         this.regenTimer += dt;
+         if (this.regenTimer >= 1.0) {
+             const regenAmount = this.getStat('regen'); // Use calculated regen
+             if (regenAmount > 0) {
+                 this.heal(regenAmount);
+             }
+             this.regenTimer -= 1.0;
+         }
+     }
+ 
+     gainXP(amount) {
+         if (this.level >= MAX_LEVEL) return;
+         this.xp += amount;
+         while (this.xp >= this.xpToNextLevel && this.level < MAX_LEVEL) {
+             this.xp -= this.xpToNextLevel;
+             this.level++;
+             this.xpToNextLevel = this.getXPForLevel(this.level);
+             this.onLevelUp();
+         }
+     }
+ 
+     onLevelUp() {
+         this.heal(this.getStat('maxHealth') * 0.25); // Use calculated maxHealth
+         // Set isLevelUp flag in game state
+         if (window.isLevelUp !== undefined) {
+             window.isLevelUp = true;
+         }
+     }
+ 
+     heal(amount) {
+         this.health = Math.min(this.getStat('maxHealth'), this.health + amount); // Use calculated maxHealth
+     }
+ 
+     addWeapon(weaponInstance) {
+         if (this.weapons.length < this.maxWeapons) {
+             this.weapons.push(weaponInstance);
+             weaponInstance.owner = this;
+             this.recalculateStats(); // Potentially needed if weapon grants stats
+             return true;
+         }
+         return false;
+     }
+ 
+     addPassive(passiveInstance) {
+         if (this.passiveItems.length < this.maxPassives) {
+             this.passiveItems.push(passiveInstance);
+             passiveInstance.owner = this;
+             this.recalculateStats();
+             return true;
+         }
+         return false;
+     }
+ 
+     // Fixed getStat method to properly calculate player stats
+     getStat(statName) {
+         // Determine the base value from defaults. Multipliers usually start at 1.
+         let baseValue = PLAYER_DEFAULT_STATS[statName] ?? (statName.endsWith('Multiplier') || statName === 'luck' ? 1.0 : 0.0);
+ 
+         let addBonus = 0;         // Total additive bonus from items
+         let multBonusPercent = 0; // Total multiplicative bonus percentage (e.g., 0.1 for +10%)
+ 
+         // Accumulate bonuses from passive items
+         this.passiveItems.forEach(item => {
+             const itemBonus = item.bonuses[statName];
+             if (itemBonus) {
+                 addBonus += itemBonus.add || 0;
+                 multBonusPercent += itemBonus.mult || 0;
+             }
+         });
+ 
+         // Calculate the final value
+         // Formula: (Base + Additive Bonus) * (1 + Multiplicative Bonus Percentage)
+         let finalValue = (baseValue + addBonus) * (1.0 + multBonusPercent);
+ 
+         // Special handling/clamping for specific stats
