    update(dt, target) {
        if (this.isGarbage) return;

        const dx = target.x - this.x;
        const dy = target.y - this.y;
        const dist = Math.sqrt(dx * dx + dy * dy);

        if (dist > 0) {
            this.x += (dx / dist) * this.speed * dt;
            this.y += (dy / dist) * this.speed * dt;
        }

        // Collision with player
        this.damageTimer -= dt;
        if (this.damageTimer <= 0 && this.checkCollision(target)) {
            target.takeDamage(this.damage, this);
            this.damageTimer = this.damageCooldown; // Reset cooldown after hitting
        }
    }

    onDeath(killer) {
        super.onDeath(killer);
        if (killer && killer.constructor.name === 'Player') {
            // Drop XP gem
            if (window.xpGems) {
                window.xpGems.push(new ExperienceGem(this.x, this.y, this.xpValue));
            }

            if (window.killCount !== undefined) {
                window.killCount++;
            }

            // Chance to drop heart based on luck
            if (killer.getStat && Math.random() < 0.01 * killer.getStat('luck')) {
                if (window.worldObjects) {
                    window.worldObjects.push(new Pickup(this.x, this.y, EMOJI.HEART, 'heal', 20));
                }
            }
        }
    }
}"}

