import { Enemy } from './Enemy.js';
import { BOSS_BASE_DAMAGE_MULTIPLIER, BOSS_BASE_HEALTH_MULTIPLIER, BOSS_INTERVAL, EMOJI, GAME_FONT_SIZE } from '../utils/constants.js';
import { Chest } from './Chest.js';
import { ExperienceGem } from './ExperienceGem.js';

export class BossEnemy extends Enemy {
    constructor(x, y) {
        const bossMultiplier = BOSS_BASE_HEALTH_MULTIPLIER + Math.floor(window.gameTime / BOSS_INTERVAL) * 10; // Bosses get tougher each time
        const typeData = {
            emoji: EMOJI.BOSS,
            healthMult: bossMultiplier,
            speedMult: 0.8 + Math.random() * 0.4, // Slightly variable speed
            damageMult: BOSS_BASE_DAMAGE_MULTIPLIER + Math.floor(window.gameTime / BOSS_INTERVAL) * 0.5,
            xpMult: bossMultiplier * 2, // Lots of XP
        };
        super(x, y, typeData, 1); // Bosses ignore standard difficulty multiplier for health
        this.size = GAME_FONT_SIZE * 3; // Boss is bigger
        this.isBoss = true;
    }

    onDeath(killer) {
        // Don't call super.onDeath() immediately if we want the body to linger
        this.isGarbage = true; // Mark for removal
        if (killer && killer.constructor.name === 'Player') {
            if (window.killCount !== undefined) {
                window.killCount++;
            }

            // Drop a chest guaranteed
            if (window.worldObjects) {
                window.worldObjects.push(new Chest(this.x, this.y));
            }

            // Drop lots of gems too
            if (window.xpGems) {
                for (let i = 0; i < 10; i++) {
                    window.xpGems.push(new ExperienceGem(
                        this.x + (Math.random() - 0.5) * 50, 
                        this.y + (Math.random() - 0.5) * 50, 
                        this.xpValue / 10
                    ));
                }
            }
        }

        // Allow normal spawns again
        if (window.currentBoss !== undefined) {
            window.currentBoss = null;
        }
        console.log("Boss defeated!");
    }

    draw(context) {
        super.draw(context);
        // Draw Boss Health Bar above it
        if (!this.isGarbage && this.health > 0) {
            const barWidth = 80;
            const barHeight = 8;
            const barX = this.x - barWidth / 2;
            const barY = this.y - this.size / 1.5 - barHeight; // Position above emoji
            const healthPercent = this.health / this.stats.health;

            context.fillStyle = '#555';
            context.fillRect(barX, barY, barWidth, barHeight);
            context.fillStyle = '#e74c3c';
            context.fillRect(barX, barY, barWidth * healthPercent, barHeight);
            context.strokeStyle = '#333';
            context.strokeRect(barX, barY, barWidth, barHeight);
        }
    }
}

