import { GameObject } from './GameObject.js';

export class Projectile extends GameObject {
    constructor(x, y, emoji, size, vx, vy, damage, pierce, duration, ownerStats) {
        super(x, y, emoji, size);
        this.vx = vx;
        this.vy = vy;
        this.damage = damage * ownerStats.damageMultiplier;
        this.pierce = pierce; // How many enemies it can hit before disappearing
        this.duration = duration; // How long it lasts in seconds
        this.lifetime = 0;
        this.ownerStats = ownerStats; // Store owner's stats at time of firing
        this.hitTargets = new Set(); // Keep track of enemies already hit by this projectile instance
    }

    update(dt) {
        this.x += this.vx * dt;
        this.y += this.vy * dt;
        this.lifetime += dt;

        if (this.lifetime >= this.duration || this.pierce <= 0) {
            this.isGarbage = true;
            return;
        }

        // Check collision with enemies
        if (window.enemies) {
            window.enemies.forEach(enemy => {
                if (this.isGarbage || enemy.isGarbage || this.hitTargets.has(enemy)) return;

                if (this.checkCollision(enemy)) {
                    const isDead = enemy.takeDamage(this.damage, window.player); // Assume player is owner for kill credit
                    this.hitTargets.add(enemy); // Mark as hit
                    this.pierce--;
                    if (this.pierce <= 0) {
                        this.isGarbage = true;
                    }
                    // Optional: Add hit effect
                }
            });
        }

        // Boundary check (remove if it goes too far off screen)
        const gameWidth = window.innerWidth * 0.95;
        const gameHeight = window.innerHeight * 0.95;
        if (this.x < -this.size * 2 || this.x > gameWidth + this.size * 2 ||
            this.y < -this.size * 2 || this.y > gameHeight + this.size * 2) {
            this.isGarbage = true;
        }
    }
}

