import { GameObject } from './GameObject.js';
import { GAME_FONT_SIZE } from '../utils/constants.js';

export class Pickup extends GameObject {
    constructor(x, y, emoji, type, value) {
        super(x, y, emoji, GAME_FONT_SIZE);
        this.type = type; // 'heal', 'magnet', etc.
        this.value = value;
        this.lifetime = 10; // Disappears after 10 seconds
        this.attractionSpeed = 300;
    }

    update(dt, target) { // Target is the player
        this.lifetime -= dt;
        if (this.lifetime <= 0) {
            this.isGarbage = true;
            return;
        }

        const pickupRadiusSq = target.pickupRadiusSq;
        const distSq = this.distanceSq(target);

        if (distSq < pickupRadiusSq && distSq > 1) {
            const dist = Math.sqrt(distSq);
            const dx = target.x - this.x;
            const dy = target.y - this.y;
            this.x += (dx / dist) * this.attractionSpeed * dt;
            this.y += (dy / dist) * this.attractionSpeed * dt;
        }

        if (this.checkCollision(target)) {
            this.applyEffect(target);
            this.isGarbage = true;
        }
    }

    applyEffect(target) {
        switch (this.type) {
            case 'heal':
                target.heal(this.value);
                break;
            case 'magnet': // Could be a temp boost pickup
                // target.applyTemporaryBuff('magnetBonus', this.value, 5); // Example
                console.log("Magnet pickup (not fully implemented)");
                break;
            // Add other pickup types
        }
    }
}

