        const distSq = this.distanceSq(target);

        if (distSq < pickupRadiusSq && distSq > 1) {
            const dist = Math.sqrt(distSq);
            const dx = target.x - this.x;
            const dy = target.y - this.y;
            this.x += (dx / dist) * this.attractionSpeed * dt;
            this.y += (dy / dist) * this.attractionSpeed * dt;
        }

        if (this.checkCollision(target)) {
            this.applyEffect(target);
            this.isGarbage = true;
        }
    }

    applyEffect(target) {
        switch (this.type) {
            case 'heal':
                target.heal(this.value);
                break;
            case 'magnet': // Could be a temp boost pickup
                // target.applyTemporaryBuff('magnetBonus', this.value, 5); // Example
                console.log("Magnet pickup (not fully implemented)");
                break;
            // Add other pickup types
        }
    }
}"}

