import { EMOJI, GAME_FONT_SIZE } from '../utils/constants.js';

export class Chest extends GameObject {
    constructor(x, y) {
        super(x, y, EMOJI.CHEST, GAME_FONT_SIZE * 1.5);
        this.lifetime = 30; // Chest persists for a while
        this.collected = false;
    }

    update(dt, target) {
        if (this.collected) return;
        this.lifetime -= dt;
        if (this.lifetime <= 0) {
            this.isGarbage = true;
            return;
        }

        if (this.checkCollision(target)) {
            this.open(target);
            this.collected = true;
            this.isGarbage = true; // Remove immediately after opening
        }
    }

    open(player) {
        console.log("Chest Opened!");
        // Grant rewards - e.g., 1-3 random level ups, or a guaranteed item/evolution
        // Simple version: grant 1 level up for now
        player.gainXP(player.xpToNextLevel); // Grant enough XP for next level
        // Could add logic here to check for possible evolutions and offer them preferentially
    }
}

