export class UpgradeableItem {
    constructor(name, emoji, maxLevel = 8) {
        this.name = name;
        this.emoji = emoji;
        this.level = 1;
        this.maxLevel = maxLevel;
        this.owner = null; // Will be set when added to player
    }

    getLevel() { return this.level; }
    isMaxLevel() { return this.level >= this.maxLevel; }

    upgrade() {
        if (!this.isMaxLevel()) {
            this.level++;
            console.log(`${this.name} upgraded to level ${this.level}`);
            this.onUpgrade(); // Apply stat changes for the new level
            return true;
        }
        return false;
    }

    // Abstract methods to be implemented by subclasses
    onUpgrade() { /* Recalculate stats based on new level */ }

    getUpgradeDescription() { 
        return `Increase ${this.name} effectiveness.`; 
    } // Default description

    getBaseUpgradeOptions(player) { // Options when getting this item for the first time
        return [{
            item: this, // The instance to potentially add
            type: this instanceof Weapon ? 'new_weapon' : 'new_passive',
            text: `Acquire ${this.name}`,
            description: this.getInitialDescription ? this.getInitialDescription() : `Adds the ${this.name}.`,
            icon: this.emoji,
            action: () => {
                if (this instanceof Weapon) player.addWeapon(this);
                else player.addPassive(this);
            }
        }];
    }

    getCurrentUpgradeOptions(player) { // Options when upgrading this existing item
        if (this.isMaxLevel()) return []; // No upgrades if max level

        return [{
            item: this,
            type: this instanceof Weapon ? 'upgrade_weapon' : 'upgrade_passive',
            text: `Upgrade ${this.name}`,
            description: this.getUpgradeDescription(),
            icon: this.emoji,
            level: this.level + 1, // Show next level
            action: () => {
                this.upgrade();
                // Check for evolution *after* upgrading
                if (this instanceof Weapon && window.checkEvolution) {
                    window.checkEvolution(player, this);
                }
            }
        }];
    }
}

