import { UpgradeableItem } from '../items/UpgradeableItem.js';

export class Weapon extends UpgradeableItem {
    constructor(name, emoji, baseCooldown, maxLevel) {
        super(name, emoji, maxLevel);
        this.baseCooldown = baseCooldown;
        this.cooldownTimer = 0;
        this.stats = {}; // Weapon specific stats like damage, area, count etc.
        this.calculateStats(); // Initial calculation
    }

    update(dt, owner) {
        this.cooldownTimer -= dt * owner.getStat('cooldownMultiplier'); // Apply global cooldown reduction
        if (this.cooldownTimer <= 0) {
            this.fire(owner);
            this.cooldownTimer = this.stats.cooldown;
        }
    }

    // Abstract - implement in specific weapons
    fire(owner) { console.warn(`${this.name} fire() not implemented`); }
    calculateStats() { console.warn(`${this.name} calculateStats() not implemented`); }

    getOwnerStats(owner) {
        // Consolidate player stats relevant to this weapon at the moment of firing
        return {
            x: owner.x,
            y: owner.y,
            damageMultiplier: owner.getStat('damageMultiplier'),
            areaMultiplier: owner.getStat('areaMultiplier'),
            durationMultiplier: owner.getStat('durationMultiplier'),
            projectileSpeedMultiplier: owner.getStat('projectileSpeedMultiplier'),
            projectileCountBonus: owner.getStat('projectileCountBonus') || 0, // Ensure it exists
            luck: owner.luck,
            lastMoveDirection: owner.lastMoveDirection,
        };
    }
}

