import { UpgradeableItem } from './UpgradeableItem.js';

export class PassiveItem extends UpgradeableItem {
    constructor(name, emoji, maxLevel, bonuses = {}) {
        super(name, emoji, maxLevel);
        // Bonuses structure: { statName: { add: value, mult: value }, ... }
        // Example: { damageMultiplier: { mult: 0.1 } } // 10% damage increase at level 1
        this.baseBonuses = bonuses; // Store the per-level definition
        this.bonuses = {}; // Actual calculated bonuses for current level
        this.calculateStats(); // Calculate for level 1 initially
    }

    applyInitialBonus(player) {
        // Called when item is first added
        this.calculateStats(); // Ensure stats are calculated for level 1
        player.recalculateStats(); // Trigger player stat update
    }

    onUpgrade() {
        this.calculateStats(); // Recalculate bonuses for the new level
        if (this.owner) {
            this.owner.recalculateStats(); // Update player stats immediately
            // Also check for evolution involving this passive
            if (window.checkEvolution) {
                window.checkEvolution(this.owner, this);
            }
        }
    }

    // Example: Needs to be specific per item based on baseBonuses
    calculateStats() {
        this.bonuses = {};
        for (const stat in this.baseBonuses) {
            const base = this.baseBonuses[stat];
            this.bonuses[stat] = {};
            if (base.add) this.bonuses[stat].add = base.add * this.level;
            if (base.mult) this.bonuses[stat].mult = base.mult * this.level;
        }
    }

    getUpgradeDescription() {
        // Generate description based on next level's bonus improvement
        // This requires defining how bonuses scale per level in baseBonuses
        // Simplified:
        let desc = `Improves ${this.name}. `;
        // Example: if it boosts damageMultiplier
        if (this.baseBonuses.damageMultiplier?.mult) {
            desc += ` (+${(this.baseBonuses.damageMultiplier.mult * 100).toFixed(0)}% Damage/Lv)`;
        }
        if (this.baseBonuses.maxHealth?.add) {
            desc += ` (+${this.baseBonuses.maxHealth.add} Max HP/Lv)`;
        }
        // Add more specific descriptions based on baseBonuses structure
        return desc;
    }

    getInitialDescription() {
        // Similar to getUpgradeDescription but for Lv 1 effect
        let desc = `Grants ${this.name}. `;
        if (this.baseBonuses.damageMultiplier?.mult) {
            desc += ` (+${(this.baseBonuses.damageMultiplier.mult * 100).toFixed(0)}% Damage)`;
        }
        if (this.baseBonuses.maxHealth?.add) {
            desc += ` (+${this.baseBonuses.maxHealth.add} Max HP)`;
        }
        return desc;
    }
}

