import { Weapon } from './Weapon.js';
import { EMOJI } from '../utils/constants.js';
import { Projectile } from '../entities/Projectile.js';

export class DaggerWeapon extends Weapon {
    static Name = "Dagger";
    static Emoji = EMOJI.WEAPON_DAGGER;
    static MaxLevel = 8;
    static Evolution = { requires: "Bracer", evolvesTo: "ThousandKnives" };

    constructor() {
        super(DaggerWeapon.Name, DaggerWeapon.Emoji, 1.0, DaggerWeapon.MaxLevel);
    }

    calculateStats() {
        this.stats = {
            damage: 5 + (this.level - 1) * 2,
            projectileSpeed: 350 + (this.level - 1) * 15,
            cooldown: Math.max(0.2, this.baseCooldown - (this.level - 1) * 0.08),
            count: 1 + Math.floor((this.level - 1) / 2), // Extra projectile every 2 levels
            pierce: 0 + Math.floor(this.level / 4), // Pierce at levels 4, 8
            duration: 1.5, // How long projectile lasts (range)
        };
    }

    fire(owner) {
        const ownerStats = this.getOwnerStats(owner);
        const count = this.stats.count + ownerStats.projectileCountBonus;
        const speed = this.stats.projectileSpeed * ownerStats.projectileSpeedMultiplier;
        const damage = this.stats.damage;
        const pierce = this.stats.pierce;
        const duration = this.stats.duration * ownerStats.durationMultiplier;
        const size = EMOJI.PROJECTILE_DAGGER.length * (1 + ownerStats.areaMultiplier - 1); // Apply area mod

        let target = owner.findNearestEnemy(800); // Target nearest enemy within range
        if (!target) target = { 
            x: owner.x + owner.lastMoveDirection.x * 100, 
            y: owner.y + owner.lastMoveDirection.y * 100 
        }; // Fire in facing direction if no target

        const dx = target.x - owner.x;
        const dy = target.y - owner.y;
        const dist = Math.sqrt(dx * dx + dy * dy);
        const dirX = dist > 0 ? dx / dist : owner.lastMoveDirection.x;
        const dirY = dist > 0 ? dy / dist : owner.lastMoveDirection.y;

        const angleStep = count > 1 ? (Math.PI / 18) : 0; // Small spread for multiple daggers
        const startAngle = Math.atan2(dirY, dirX) - (angleStep * (count - 1) / 2);

        for (let i = 0; i < count; i++) {
            const angle = startAngle + i * angleStep;
            const vx = Math.cos(angle) * speed;
            const vy = Math.sin(angle) * speed;

            // Create projectile and add to global projectiles array
            if (window.projectiles) {
                window.projectiles.push(
                    new Projectile(
                        owner.x, 
                        owner.y, 
                        EMOJI.PROJECTILE_DAGGER, 
                        size, 
                        vx, 
                        vy, 
                        damage, 
                        pierce, 
                        duration, 
                        ownerStats
                    )
                );
            }
        }
    }

    getUpgradeDescription() {
        // Example: Describe the next level's improvement
        let desc = `Lv${this.level+1}: `;
        if (this.level % 2 === 1) desc += "+1 Projectile. "; // Levels 2, 4, 6, 8
        else if (this.level === 3 || this.level === 7) desc += "+1 Pierce. "; // Levels 4, 8
        else desc += "+Damage/Speed. ";
        return desc + `(Cooldown: ${Math.max(0.2, this.baseCooldown - this.level * 0.08).toFixed(2)}s)`;
    }
}

