import { Weapon } from './Weapon.js';
import { EMOJI } from '../utils/constants.js';

export class GarlicWeapon extends Weapon {
    static Name = "Garlic";
    static Emoji = EMOJI.WEAPON_GARLIC;
    static MaxLevel = 8;
    static Evolution = { requires: "Pummarola", evolvesTo: "SoulEater" };

    constructor() {
        super(GarlicWeapon.Name, GarlicWeapon.Emoji, 1.5, GarlicWeapon.MaxLevel); // Cooldown affects damage tick rate
        this.auraRadius = 0; // Calculated in stats
        this.knockback = 0; // Calculated
        this.lastHitTargets = new Map(); // track target -> last hit time
    }

    calculateStats() {
        this.stats = {
            damage: 2 + (this.level - 1) * 1,
            cooldown: Math.max(0.5, this.baseCooldown - (this.level - 1) * 0.1), // Faster ticks
            area: 60 + (this.level - 1) * 10, // Radius
            knockback: 5 + this.level * 1,
        };
    }

    // Garlic doesn't fire projectiles, it applies damage in an area continuously (simulated by update)
    fire(owner) { /* Cooldown timer used for damage ticks */ }

    update(dt, owner) {
        // Continuous effect, not tied to 'fire' cooldown, but uses weapon timer concept for damage ticks
        this.cooldownTimer -= dt * owner.getStat('cooldownMultiplier');
        if (this.cooldownTimer <= 0) {
            const ownerStats = this.getOwnerStats(owner);
            const radius = this.stats.area * ownerStats.areaMultiplier;
            const radiusSq = radius * radius;
            const damage = this.stats.damage; // Low base damage, ticks often
            const knockbackPower = this.stats.knockback;

            // Get enemies from owner's findEnemiesInRadius method
            const enemies = owner.findEnemiesInRadius(radius);

            enemies.forEach(enemy => {
                if (enemy.isGarbage) return;
                const distSq = owner.distanceSq(enemy);
                if (distSq <= radiusSq) {
                    // Apply damage
                    enemy.takeDamage(damage * ownerStats.damageMultiplier, owner);
                    // Apply knockback
                    if (knockbackPower > 0 && distSq > 0) {
                        const dist = Math.sqrt(distSq);
                        const pushForce = knockbackPower * (1 - dist / radius); // Stronger closer
                        const pushX = (enemy.x - owner.x) / dist * pushForce * dt * 10; // dt scaling might need tuning
                        const pushY = (enemy.y - owner.y) / dist * pushForce * dt * 10;
                        enemy.x += pushX;
                        enemy.y += pushY;
                    }
                }
            });
            this.cooldownTimer = this.stats.cooldown; // Reset tick timer
        }

        // Store the visual effect radius
        this.auraRadius = this.stats.area * this.getOwnerStats(owner).areaMultiplier;
    }

    drawAura(context, owner) { // Helper to draw the aura
        if (this.auraRadius > 0) {
            context.beginPath();
            context.arc(owner.x, owner.y, this.auraRadius, 0, Math.PI * 2);
            context.fillStyle = 'rgba(255, 255, 255, 0.1)'; // Semi-transparent white aura
            context.fill();
            // Maybe add particle effect?
        }
    }

    getUpgradeDescription() {
        return `Lv${this.level+1}: +Damage/Area. (Tick Rate: ${Math.max(0.5, this.baseCooldown - this.level * 0.1).toFixed(2)}s)`;
    }
}

