import { Weapon } from './Weapon.js';
import { EMOJI } from '../utils/constants.js';

export class WhipWeapon extends Weapon {
    static Name = "Whip";
    static Emoji = EMOJI.WEAPON_WHIP;
    static MaxLevel = 8;
    static Evolution = { requires: "HollowHeart", evolvesTo: "BloodyTear" };

    constructor() {
        super(WhipWeapon.Name, WhipWeapon.Emoji, 1.8, WhipWeapon.MaxLevel);
        this.whipHitboxes = []; // Store temporary hitboxes
    }

    calculateStats() {
        this.stats = {
            damage: 15 + (this.level - 1) * 5,
            cooldown: Math.max(0.8, this.baseCooldown - (this.level - 1) * 0.1),
            area: 100 + (this.level - 1) * 15, // Length of whip
            count: 1 + Math.floor(this.level / 4), // Second whip at level 4, maybe hit behind at 8?
            pierce: Infinity, // Whip hits all targets in its path
        };
    }

    fire(owner) {
        const ownerStats = this.getOwnerStats(owner);
        const damage = this.stats.damage;
        const area = this.stats.area * ownerStats.areaMultiplier;
        const count = this.stats.count + Math.floor(ownerStats.projectileCountBonus / 2); // Whip count less affected by bonus
        const width = 20 * ownerStats.areaMultiplier; // Width of the whip lash

        const facingRight = owner.lastMoveDirection.x >= 0; // Determine direction

        for (let i = 0; i < count; i++) {
            // Alternate sides or add behind based on count/level
            const attackRight = (i % 2 === 0) ? facingRight : !facingRight;
            const x = owner.x + (attackRight ? 10 : -10); // Start slightly offset
            const y = owner.y;
            const hitboxWidth = area;
            const hitboxHeight = width;

            // Create a temporary hitbox object for collision detection
            const hitbox = {
                x: x + (attackRight ? area / 2 : -area / 2), // Center of hitbox
                y: y,
                width: hitboxWidth,
                height: hitboxHeight,
                damage: damage,
                ownerStats: ownerStats,
                lifetime: 0.2, // How long the hitbox persists
                hitTargets: new Set(), // Track hits for this specific whip crack
                isGarbage: false,
                emoji: attackRight ? EMOJI.PROJECTILE_WHIP_R : EMOJI.PROJECTILE_WHIP_L,
                size: Math.max(hitboxWidth, hitboxHeight) * 0.5, // Approx size for draw
                attackRight: attackRight,

                update: function(dt) {
                    this.lifetime -= dt;
                    if (this.lifetime <= 0) {
                        this.isGarbage = true;
                        return;
                    }

                    // Collision check during its short lifetime
                    if (window.enemies) {
                        window.enemies.forEach(enemy => {
                            if (this.isGarbage || enemy.isGarbage || this.hitTargets.has(enemy)) return;

                            // AABB check for the hitbox
                            const halfW = this.width / 2;
                            const halfH = this.height / 2;
                            const enemyHalfSize = enemy.size / 2;
                            if (this.x - halfW < enemy.x + enemyHalfSize &&
                                this.x + halfW > enemy.x - enemyHalfSize &&
                                this.y - halfH < enemy.y + enemyHalfSize &&
                                this.y + halfH > enemy.y - enemyHalfSize)
                            {
                                enemy.takeDamage(this.damage * this.ownerStats.damageMultiplier, window.player);
                                this.hitTargets.add(enemy);
                                // Whip has infinite pierce implicitly
                            }
                        });
                    }
                },

                draw: function(ctx) { // Draw the whip crack effect
                    if (!this.isGarbage) {
                        // Simple line or series of emojis to represent the whip
                        ctx.fillStyle = 'rgba(200, 200, 200, 0.5)';
                        ctx.fillRect(this.x - this.width / 2, this.y - this.height / 2, this.width, this.height);
                        // Or draw emoji repeatedly along the path?
                        // ctx.font = `16px 'Segoe UI Emoji'`;
                        // ctx.fillText(this.emoji, this.x, this.y);
                    }
                }
            };
            this.whipHitboxes.push(hitbox);
        }
    }

    update(dt, owner) {
        // Update cooldown timer
        super.update(dt, owner); // Handles cooldown and calls fire()

        // Update and filter active hitboxes
        this.whipHitboxes = this.whipHitboxes.filter(hb => {
            hb.update(dt);
            return !hb.isGarbage;
        });
    }

    // Need a way to draw these temporary hitboxes
    drawHitboxes(context) {
        this.whipHitboxes.forEach(hb => hb.draw(context));
    }

    getUpgradeDescription() {
        let desc = `Lv${this.level+1}: +Damage/Area. `;
        if (this.level === 3) desc += "+1 Whip Strike. "; // Level 4
        return desc + `(Cooldown: ${Math.max(0.8, this.baseCooldown - this.level * 0.1).toFixed(2)}s)`;
    }
}

