// Format time in seconds to MM:SS format
export function formatTime(totalSeconds) {
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = Math.floor(totalSeconds % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
}

// Shuffle array in place
export function shuffleArray(array) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
}

// Check for weapon evolution
export function checkEvolution(player, upgradedItem) {
    console.log("Checking evolution possibilities...");
    let evolutionOccurred = false;

    for (let i = 0; i < player.weapons.length; i++) {
        const weapon = player.weapons[i];
        if (weapon.isEvolved || !weapon.constructor.Evolution) continue; // Skip evolved or non-evolving

        const evoInfo = weapon.constructor.Evolution;
        const requiredPassiveName = evoInfo.requires;
        const evolvedClassName = evoInfo.evolvesTo; // Name of the evolved class as a string

        // Check if this weapon is max level AND the required passive exists and is *any* level
        if (weapon.isMaxLevel() && player.passiveItems.some(p => p.name === requiredPassiveName)) {
            console.log(`Evolution condition met for ${weapon.name} -> ${evolvedClassName}!`);

            // Find the class constructor by name
            let EvolvedClass = null;

            // Import the evolved weapon class dynamically
            import(`../weapons/${evolvedClassName}.js`)
                .then(module => {
                    EvolvedClass = module[evolvedClassName];
                    if (EvolvedClass) {
                        const evolvedWeapon = new EvolvedClass(weapon); // Pass base weapon if needed by constructor
                        evolvedWeapon.owner = player;
                        player.weapons[i] = evolvedWeapon; // Replace the base weapon

                        console.log(`${weapon.name} evolved into ${evolvedWeapon.name}!`);
                        evolutionOccurred = true;

                        // Update UI if needed
                        if (window.updateUI) {
                            window.updateUI();
                        }
                    } else {
                        console.error(`Could not find Evolved Class: ${evolvedClassName}`);
                    }
                })
                .catch(error => {
                    console.error(`Error loading evolved weapon class: ${error}`);
                });

            break; // Only allow one evolution per level up event
        }
    }

    return evolutionOccurred;
}

