            upgrade.action(); // Perform the upgrade action
            resumeGame();
        };
        upgradeOptionsContainer.appendChild(button);
    });

    levelUpScreen.classList.remove('hidden');
}

function update(dt) {
    // Update global gameTime for other modules
    window.gameTime = gameTime;

    if (isGameOver || isPaused || isLevelUp) {
        // Special case: Allow gem attraction even when paused/leveling up
        if (player && xpGems.length > 0) {
            xpGems.forEach(gem => gem.update(dt * 3, player)); // Faster attraction when paused
        }
        return;
    }

    gameTime += dt;
    difficultyTimer += dt;
    spawnTimer += dt;

    // --- Difficulty & Spawning ---
    if (difficultyTimer > 10) { // Adjust difficulty every 10 seconds
        // Spawn faster over time, minimum interval 0.1s
        currentSpawnInterval = Math.max(0.1, baseSpawnInterval * Math.pow(0.95, gameTime / 60));
        difficultyTimer = 0;
    }

    if (spawnTimer >= currentSpawnInterval) {
        const spawnCount = 1 + Math.floor(gameTime / 30); // Spawn more enemies over time
        for(let i=0; i<spawnCount; i++) {
            spawnEnemy();
        }
        spawnTimer = 0;
    }

    // --- Boss Spawning ---
    if (gameTime >= nextBossTime && !currentBoss) {
        spawnBoss();
        nextBossTime += BOSS_INTERVAL; // Schedule next boss
    }

    // --- Update Game Objects ---
    player.update(dt, keys);
    enemies.forEach(e => e.update(dt, player));
    projectiles.forEach(p => p.update(dt));
    xpGems.forEach(gem => gem.update(dt, player));
    worldObjects.forEach(obj => obj.update(dt, player));

    // --- Remove Garbage Objects ---
    enemies = enemies.filter(e => !e.isGarbage);
    projectiles = projectiles.filter(p => !p.isGarbage);
    xpGems = xpGems.filter(g => !g.isGarbage);
    worldObjects = worldObjects.filter(o => !o.isGarbage);

    // --- Handle Level Up Screen Trigger ---
    if (isLevelUp) {
        presentLevelUpOptions(); // This function will set the pause state correctly
        isLevelUp = false; // Reset trigger flag
    }

    updateUI();
}

function draw() {
    ctx.clearRect(0, 0, GAME_WIDTH, GAME_HEIGHT);
    ctx.fillStyle = '#2d2d3a'; // Background
    ctx.fillRect(0, 0, GAME_WIDTH, GAME_HEIGHT);

    // Set default font for this frame (objects might change it)
    ctx.font = `24px 'Segoe UI Emoji'`;
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';

    // Draw Objects (order matters for layering)
    xpGems.forEach(gem => gem.draw(ctx));
    worldObjects.forEach(obj => obj.draw(ctx)); // Chests, pickups
    enemies.forEach(enemy => enemy.draw(ctx)); // Draw enemies before player projectiles maybe?
    projectiles.forEach(projectile => projectile.draw(ctx));
    player.draw(ctx); // Draw player on top of most things

    // Draw weapon effects that aren't projectiles (like Garlic aura, Whip hitboxes)
    player.weapons.forEach(weapon => {
        if (weapon.drawAura) weapon.drawAura(ctx, player);
        if (weapon.drawHitboxes) weapon.drawHitboxes(ctx);
    });
}

function updateUI() {
    if (!player) return;

    // Health
    healthValueUI.textContent = Math.ceil(player.health);
    maxHealthValueUI.textContent = player.maxHealth;
    healthBarUI.style.width = `${(player.health / player.maxHealth) * 100}%`;

    // Level & XP
    levelValueUI.textContent = player.level;
    if (player.level < 99) {
        xpValueUI.textContent = player.xp;
        xpNextLevelValueUI.textContent = player.xpToNextLevel;
        xpBarUI.style.width = `${(player.xp / player.xpToNextLevel) * 100}%`;
    } else {
        xpValueUI.textContent = "MAX";
        xpNextLevelValueUI.textContent = "";
        xpBarUI.style.width = `100%`;
        xpBarUI.style.backgroundColor = '#f1c40f'; // Gold color for max level
    }

    // Timer & Kills
    timerValueUI.textContent = formatTime(gameTime);
    killCountValueUI.textContent = killCount;

    // Weapon & Passive Icons
    weaponIconsUI.innerHTML = player.weapons.map(w =>
        `<span class="uiIcon" title="${w.name}">${w.emoji}<span class="uiItemLevel">${w.isEvolved ? 'MAX' : 'Lv'+w.level}</span></span>`
    ).join(' ');
    passiveIconsUI.innerHTML = player.passiveItems.map(p =>
        `<span class="uiIcon" title="${p.name}">${p.emoji}<span class="uiItemLevel">Lv${p.level}</span></span>`
    ).join(' ');
}

function gameOver() {
    isGameOver = true;
    if (animationFrameId) cancelAnimationFrame(animationFrameId);
    finalTimeUI.textContent = formatTime(gameTime);
    finalLevelUI.textContent = player.level;
    finalKillsUI.textContent = killCount;
    gameOverScreen.classList.remove('hidden');
}

function pauseGame(forLevelUp = false) {
    if (isGameOver) return;
    isPaused = true;
    if (!forLevelUp) {
        pauseScreen.classList.remove('hidden');
    }
    // Don't cancel animation frame, just stop updating game logic
}

function resumeGame() {
    if (isGameOver) return;
    isPaused = false;
    isLevelUp = false; // Ensure this is reset
    levelUpScreen.classList.add('hidden');
    pauseScreen.classList.add('hidden');
    lastTime = performance.now(); // Prevent large deltaTime jump
    // Restart loop if it was somehow cancelled
    if (!animationFrameId) { // Should not happen if just paused, but safety check
        gameLoop(lastTime);
    }
}

// --- Main Game Loop ---
function gameLoop(timestamp) {
    deltaTime = (timestamp - lastTime) / 1000;
    lastTime = timestamp;

    // Clamp deltaTime to prevent spiral of death if tabbed out
    deltaTime = Math.min(deltaTime, 0.1); // Max delta 100ms (10fps)

    update(deltaTime);
    draw();

    // Request next frame if game isn't over
    if (!isGameOver) {
        animationFrameId = requestAnimationFrame(gameLoop);
    } else {
        animationFrameId = null; // Clear ID on game over
    }
}

// --- Event Listeners ---
window.addEventListener('keydown', (e) => {
    keys[e.key.toLowerCase()] = true;
    // Pause Toggle
    if (e.key.toLowerCase() === 'p') {
        if (isPaused && !isLevelUp) { // Can only unpause via P if it wasn't a levelup pause
            resumeGame();
        } else if (!isPaused && !isLevelUp) { // Can only pause via P if not already paused/leveling
            pauseGame();
        }
    }
});

window.addEventListener('keyup', (e) => {
    keys[e.key.toLowerCase()] = false;
});

// Start/Restart Buttons
startButton.addEventListener('click', () => {
    init();
});

restartButton.addEventListener('click', () => {
    init();
});

resumeButton.addEventListener('click', () => {
    resumeGame();
});

// Handle window resize
window.addEventListener('resize', () => {
    canvas.width = Math.min(window.innerWidth * 0.95, 1280);
    canvas.height = Math.min(window.innerHeight * 0.95, 720);
    if (player) { // Adjust player boundary limits if game is running
        player.x = Math.max(player.size / 2, Math.min(canvas.width - player.size / 2, player.x));
        player.y = Math.max(player.size / 2, Math.min(canvas.height - player.size / 2, player.y));
    }
    // No need to redraw immediately unless game is active and not paused
    if (player && !isGameOver && !isPaused && !isLevelUp) {
        draw();
    }
});

// Export game functions for other modules
export { init, gameOver, pauseGame, resumeGame, updateUI };"}

